import React, { Component } from 'react';
import { useRouteMatch } from 'react-router-dom';

class ListarUsuarios extends Component{
    constructor(props) {
        super(props);
        this.state = {
            listaUsuarios: [],
        };
    }
    componentDidMount() {
        fetch('http://127.0.0.1:4000/api/usuarios/')
            .then(res => res.json())
            .then(obj => this.setState({listaUsuarios: obj}));
    }
    render() {
        /*let promesaHTTP = window.fetch('http://127.0.0.1:4000/api/usuarios/');
            
            //En las funciones flecha, no es necesario bind()
            promesaHTTP.then((res) => {
                let promesaJSON = res.json();
                promesaJSON.then((obj) =>{
                    this.setState({
                        listarUsuarios: obj
                    })
                })
            });*/
        return (
            <div>

                <h2>Lista de usuarios</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Email</th>
                            <th>Password</th>
                            <th>Nombre</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {this.state.listaUsuarios.map( usu =>
                            <React.Fragment>
                                <td>key={usu.email}</td>
                                <td>key={usu.password}</td>
                                <td>key={usu.nombre}</td>
                            </React.Fragment>
                            )}
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}
export default ListarUsuarios;
