// Componente Raiz del proyecto
import React, { Component } from 'react';  // importamos mod React
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import './App.css'
import logo from '../assets/img/logo.jpg';
import CrearUsuario from './CrearUsuario';
import ListarUsuarios from './ListarUsuarios';

class App extends Component {
    render() {
        return (
            <Router>
                <nav class="navbar navbar-expand-lg navbar-light bg-white shadow-sm justify-content-between">
                    <div className="brand">
                        <a class="navbar-brand" href="#">
                            <img src={logo} width="60" height="60"></img>
                        </a>
                        <h1 className="brand-title">Operaciones CRUD</h1>
                    </div>
                    <div>
                        <ul class="nav navbar-nav navbar-left">
                            <li class="nav-item">
                                <a class="nav-link py-1 px-0" href="/">Crear Usuario</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link py-1 px-0" href="/registro">Listar Usuarios</a>
                            </li>
                        </ul>
                    </div>
                </nav>
                <Route path="/" exact component={ ListarUsuarios } />
                <Route path="/registro" component={ CrearUsuario } />
            </Router>
        );
    }
}
export default App;
