import React, { Component } from 'react';
import './home.css';
import createIcon from '../assets/img/folder-plus-solid.svg';
import readIcon from '../assets/img/book-open-solid.svg';
import deleteIcon from '../assets/img/trash-alt-solid.svg';
import updateIcon from '../assets/img/redo-alt-solid.svg';

class Home extends Component {
    render () {
        return (
            <div className="home-container">
                <h1 className="home-title">Bienvenido a la web de operaciones CRUD con MERN</h1>
                <div className="home-icons">
                    <div className="crud-icon">
                        <img src={createIcon} alt="Crear"/>
                        <p className="icon-title">CREATE</p>
                    </div>
                    <div className="crud-icon">
                        <img src={readIcon} alt="Leer"/>
                        <p className="icon-title">READ</p>
                    </div>
                    <div className="crud-icon">
                        <img src={updateIcon} alt="Modificar"/>
                        <p className="icon-title">UPDATE</p>
                    </div>
                    <div className="crud-icon">
                        <img src={deleteIcon} alt="Eliminar"/>
                        <p className="icon-title">DELETE</p>
                    </div>
                </div>
            </div>
        );
    }
    
}

export default Home;
