import React, { Component } from 'react';
import './home.css';
import createIcon from '../assets/img/folder-plus-solid.svg';
import readIcon from '../assets/img/book-open-solid.svg';
import updateIcon from '../assets/img/redo-alt-solid.svg';
import deleteIcon from '../assets/img/trash-alt-solid.svg';


class Home extends Component {
    render () {
        return (
            <div className="home-container">
                <h1 className="home-title">Bienvenido a la web de operaciones CRUD con MERN</h1>
                <div className="home-icons">
                    <div className="section-icon">
                        <img src={readIcon} className="icon-img" alt="Crear"/>
                        <p className="icon-text">READ</p>
                    </div>
                    <div className="section-icon">
                        <img src={readIcon} className="icon-img" alt="Crear"/>
                        <p className="icon-text">READ</p>
                    </div>
                    <div className="section-icon">
                        <img src={updateIcon} className="icon-img" alt="Modificar"/>
                        <p className="icon-text">UPDATE</p>
                    </div>
                    <div className="section-icon">
                        <img src={deleteIcon} className="icon-img" alt="Eliminar"/>
                        <p className="icon-text">DELETE</p>
                    </div>
                </div>
            </div>
        );
    }
    
}

export default Home;
