import React, { Component } from 'react';

class ListarUsuarios extends Component{
    constructor(props) {
        super(props);
        this.state = {
            obj: null
        };
    }
    componentDidMount() {
        fetch('http://127.0.0.1:4000/api/usuarios/')
            .then(res => res.json())
            .then(obj => this.setState({listaUsuarios: obj}));
    }
    render() {
